$(document).ready(function(){$("#userPhotoWindow").kendoWindow({width:"600px",title:"User Photo",modal:!0,visible:!1,actions:["Pin","Minimize","Maximize","Close"]});$("#btnUploadPhoto").click(function(){$("#userPhotoWindow").data("kendoWindow").refresh($("#btnUploadPhoto").data("url"));$("#userPhotoWindow").data("kendoWindow").open();$("#userPhotoWindow").data("kendoWindow").center()})});